jQuery(document).ready(function () {
	copyForm();
	initOpenClose();
	initMobileNav();
	initTouchNav();
	initCustomForms();
	initValidation();
	initFitVids();
	initSlickSlider();
	if(jQuery(window).width() >= 1024){
		initScrollClass();
	}
});

jQuery(window).load(function () {
	initRetinaCover();
});


function copyForm() {
	var form = $('#feedback-form');

	$('.main-banner .opener-form').on('click', function(){
		$('#header #feedback-form').remove();

		jcf.destroyAll();

		form.appendTo('.main-banner .slide');

		initValidation();
		initCustomForms();
	});

	$('#header .opener-form').on('click', function(){
		$('.main-banner #feedback-form').remove();

		jcf.destroyAll();

		form.appendTo('.feedback-form-add');

		initValidation();
		initCustomForms();
	});

	$('#header .opener-form').on('click', function(){
		$('.widget-form #feedback-form').remove();

		jcf.destroyAll();

		form.appendTo('.feedback-form-add');

		initValidation();
		initCustomForms();
	});

	$('.widget-form .opener-form').on('click', function(){
		$('#header #feedback-form').remove();

		jcf.destroyAll();

		form.appendTo('.widget-form .slide');

		initValidation();
		initCustomForms();
	});
}

// open-close init
function initOpenClose() {
	jQuery('.feedback-form').openClose({
		hideOnClickOutside: true,
		activeClass: 'active-form',
		opener: '.opener-form',
		slider: '.slide',
		effect: 'none'
	});

	jQuery('#header').openClose({
		hideOnClickOutside: true,
		activeClass: 'active-form',
		opener: '.opener-form',
		slider: '.feedback-form-add',
		effect: 'none'
	});

	jQuery('.widget-form').openClose({
		hideOnClickOutside: true,
		activeClass: 'active',
		opener: '.opener-form',
		slider: '.slide',
		effect: 'none'
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		menuDrop: '.nav-drop'
	});

	$('#nav .drop').prev('a').append('<i class="ico"></i>');
}

function initScrollClass() {
	var scrollClass = 'page-scrolled';
	var body = jQuery('body');
	var win = jQuery(window);
	win.on('scroll resize orentationchange', function(){
		if (win.scrollTop() > 0) {
			body.addClass(scrollClass);
		} else {
			body.removeClass(scrollClass);
		}
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('#nav').each(function(){
		new TouchNav({
			navBlock: this,
			menuDrop: '.drop'
		});
	});
}

// Slick Slider
function initSlickSlider(){
	$('.testimonials-slider').slick({
		fade: true,
		slide: '.slide',
		infinite: true,
		draggable: true,
		swipe: true,
		speed: 300,
		dots: true,
		arrows: false
	});
};

// initialize custom form elements
function initCustomForms() {
	jcf.replaceAll();
}

function initRetinaCover() {
	jQuery('.bg-stretch').retinaCover();
}

// handle flexible video size
function initFitVids() {
	jQuery('#wrapper').fitVids();
}

function initMap() {
	google.maps.event.addDomListener(window, 'load', initialize);

	function initialize() {
		var mapOptions = {
			zoom: 16,
			center: new google.maps.LatLng(43.564084, -80.281702),
			scrollwheel: false,
			navigationControl: true,
			mapTypeControl: false,
			scaleControl: true,
			draggable: !("ontouchend" in document),
			disableDoubleClickZoom: false,
			styles: [{
					"featureType": "administrative.country",
					"elementType": "geometry",
					"stylers": [
						{
							"visibility": "simplified"
						},
						{
							"hue": "#ff0000"
						}
					]
				}
			]
		};

		var mapElement = document.getElementById('map');

		var map = new google.maps.Map(mapElement, mapOptions);

		mapIconImage = (window.devicePixelRatio > 1.5) ? 'images/map-mark@2x.png' : 'images/map-mark.png';
		var myIcon = new google.maps.MarkerImage(mapIconImage, null, null, null, new google.maps.Size(40,61));

		var beachMarker = new google.maps.Marker({
				position: new google.maps.LatLng(43.563885, -80.279800),
				flat: true,
				map: map,
				icon: myIcon
		});
	}
}

function initValidation() {
	var formBlock = $('#feedback-form').find('form'),
			formName = formBlock.find('.full-name'),
			formEmail = formBlock.find('input[type=email]'),
			formPhone = formBlock.find('.phone'),
			formTextarea = formBlock.find('textarea');

	formName.on('blur', function() {
			if($(this).val().match(/^[A-z0-9_\s\w]{2,255}$/g)) {
					$(this).removeClass('error');
			}
			else {
					$(this).addClass('error');
			}
	});

	formName.on('focus', function() {
		$(this).removeClass('error');
	});

	formEmail.on('blur', function() {
			if($(this).val().match(/^[A-z0-9_]+@[A-z]{2,255}\.[A-z]+?$/g)) {
					$(this).removeClass('error');
			}
			else {
					$(this).addClass('error');
			}
	});

	formEmail.on('focus', function() {
		$(this).removeClass('error');
	});

	formPhone.on('blur', function() {
			if($(this).val().length > 3) {
					$(this).removeClass('error');
			}
			else {
					$(this).addClass('error');
			}
	});

	formPhone.on('focus', function() {
		$(this).removeClass('error');
	});

	formTextarea.on('blur', function() {
			if($(this).val().match(/^.{2,30000}$/gm)) {
					$(this).removeClass('error');
			}
			else {
					$(this).addClass('error');
			}
	});

	formTextarea.on('focus', function() {
		$(this).removeClass('error');
	});

	formBlock.on('submit', function(event) {

		if(formName.val().length == 0 || formName.hasClass('error')) {
			event.preventDefault();
			formName.addClass('error');
		}
		if(formPhone.val().length == 0 || formPhone.hasClass('error')) {
			event.preventDefault();
			formPhone.addClass('error');
		}
		if(formEmail.val().length == 0 || formEmail.hasClass('error')) {
			event.preventDefault();
			formEmail.addClass('error');
		}
		if(formTextarea.val().length == 0 || formTextarea.hasClass('error')) {
			event.preventDefault();
			formTextarea.addClass('error');
		}

	});
}

